import { async } from "@firebase/util";
import { useState } from "react";

import FormInput from "../form-input/form-input.component";
import Button from "../button/button.component";

import { createAuthUserWithEmailAndPassword, createUserDocumentFromAuth } from "../../utils/firebase/firebase.utils";

import './sign-up-form.styles.scss'

const defaultFormFields = {
    displayName: '',
    email: '',
    password: '',
    confirmPassowrd:''
}
const SignUpForm = () => {
    const [formFields, setFormFields] = useState(defaultFormFields);
    const {displayName, email, password, confirmPassword} = formFields;

    const resetFormFields = () => {
        setFormFields(defaultFormFields)
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        if(password !== confirmPassword) {
            alert("password do not match")
            return;
        }

        try {
            const {user} = await createAuthUserWithEmailAndPassword(email, password)
            
            await createUserDocumentFromAuth(user, {displayName});
            resetFormFields();
        } catch (err) {
            if(err.code === 'auth/email already in use') {
                alert('Cannot create user, email already in use');
            } else {
                console.log('user created encounter an error', err )
            }
        }
    }

    const handleChange = (event) => {
        const {name, value}  = event.target;

        setFormFields({...formFields, [name]: value})
    };
    
    return(
        <div className="sign-up-container">
            <h2>Don't have an account?</h2>
            <span>
                Sign up with your email and password
            </span>
            <form onSubmit={handleSubmit }>
                <FormInput label="Display Name" type="text" require onChange={handleChange} name="displayName" value={displayName}/>

                <FormInput label="Email" type="email" require onChange={handleChange} name="email" value={email}/>

                <FormInput label="Password" type="password" require onChange={handleChange} name="password" value={password}/>

                <FormInput label="Confirm Password" type="password" require onChange={handleChange} name="confirmPassword" value={confirmPassword}/>
                <button type="submit">Sign Up</button>
            </form>
        </div>
    )
}

export default SignUpForm;
